import loginApi from '@/api/login'

export default {
  namespaced: true,
  state () {
    return {
      // 个人权证相关 userId
      userinfo: {
        userId: '',
        username: '',
        email: ''
      }
    }
  },
  mutations: {
    setUser (state, user) {
      state.userinfo = user
    },
    cleanUser (state) {
      state.userinfo = { userId: '', username: '', email: '' }
    }
  }, // 类似于method
  actions: {
    async loginUser ({ commit }, userCredentials) {
      const res = await loginApi(userCredentials.email, userCredentials.password)
      if (res) {
        commit('setUser', {
          userId: res.userId,
          username: res.username,
          email: res.email
        })
      }
    }
  }, // 提供异步的
  getters: {}// 提供一些基于state 所派生出来的一些属性
}
