// 此处存放登录相关的接口请求
import request from '@/utils/request'

export const loginApi = (username, password) => {
  console.log('Username:', username) // 确保用户名值不为空
  console.log('Password:', password)
  return request.get('/users', {
    username,
    password
  })
}

export default loginApi
