import Vue from 'vue';
import VueRouter from 'vue-router';
import Login from '@/views/login';
import Layout from '@/views/layout';
import Article from '@/views/article';
Vue.use(VueRouter);
const router = new VueRouter({
  routes: [{
    path: '/login',
    component: Login
  }, {
    path: '/layout',
    component: Layout
  }, {
    path: '/article',
    component: Article
  }]
});
export default router;
